from abc import abstractmethod
from typing import List, Dict, Tuple

import pyqtgraph as pg

from vnpy.trader.ui import QtCore, QtGui, QtWidgets
from vnpy.trader.object import BarData

from .base import BLACK_COLOR, UP_COLOR, DOWN_COLOR, PEN_WIDTH, BAR_WIDTH
from .manager import BarManager


class ChartItem(pg.GraphicsObject):
    """"""

    def __init__(self, manager: BarManager) -> None:
        """"""
        super().__init__()

        self._manager: BarManager = manager

        self._bar_picutures: Dict[int, QtGui.QPicture] = {}
        self._item_picuture: QtGui.QPicture = None

        self._black_brush: QtGui.QBrush = pg.mkBrush(color=BLACK_COLOR)

        self._up_pen: QtGui.QPen = pg.mkPen(
            color=UP_COLOR, width=PEN_WIDTH
        )
        self._up_brush: QtGui.QBrush = pg.mkBrush(color=UP_COLOR)

        self._down_pen: QtGui.QPen = pg.mkPen(
            color=DOWN_COLOR, width=PEN_WIDTH
        )
        self._down_brush: QtGui.QBrush = pg.mkBrush(color=DOWN_COLOR)

        self._rect_area: Tuple[float, float] = None

        # Very important! Only redraw the visible part and improve speed a lot.
        self.setFlag(self.GraphicsItemFlag.ItemUsesExtendedStyleOption)

        # Force update during the next paint
        self._to_update: bool = False

    @abstractmethod
    def _draw_bar_picture(self, ix: int, bar: BarData) -> QtGui.QPicture:
        """
        Draw picture for specific bar.
        """
        pass

    @abstractmethod
    def boundingRect(self) -> QtCore.QRectF:
        """
        Get bounding rectangles for item.
        """
        pass

    @abstractmethod
    def get_y_range(self, min_ix: int = None, max_ix: int = None) -> Tuple[float, float]:
        """
        Get range of y-axis with given x-axis range.

        If min_ix and max_ix not specified, then return range with whole data set.
        """
        pass

    @abstractmethod
    def get_info_text(self, ix: int) -> str:
        """
        Get information text to show by cursor.
        """
        pass

    def update_history(self, history: List[BarData]) -> None:
        """
        Update a list of bar data.
        """
        self._bar_picutures.clear()

        bars: List[BarData] = self._manager.get_all_bars()
        for ix, bar in enumerate(bars):
            self._bar_picutures[ix] = None

        self.update()

    def update_bar(self, bar: BarData) -> None:
        """
        Update single bar data.
        """
        ix: int = self._manager.get_index(bar.datetime)

        self._bar_picutures[ix] = None

        self.update()

    def update(self) -> None:
        """
        Refresh the item.
        """
        if self.scene():
            self._to_update = True
            self.scene().update()

    def paint(
        self,
        painter: QtGui.QPainter,
        opt: QtWidgets.QStyleOptionGraphicsItem,
        w: QtWidgets.QWidget
    ) -> None:
        """
        Reimplement the paint method of parent class.

        This function is called by external QGraphicsView.
        """
        rect = opt.exposedRect

        min_ix: int = int(rect.left())
        max_ix: int = int(rect.right())
        max_ix: int = min(max_ix, len(self._bar_picutures))

        rect_area: tuple = (min_ix, max_ix)
        if (
            self._to_update
            or rect_area != self._rect_area
            or not self._item_picuture
        ):
            self._to_update = False
            self._rect_area = rect_area
            self._draw_item_picture(min_ix, max_ix)

        self._item_picuture.play(painter)

    def _draw_item_picture(self, min_ix: int, max_ix: int) -> None:
        """
        Draw the picture of item in specific range.
        """
        self._item_picuture = QtGui.QPicture()
        painter: QtGui.QPainter = QtGui.QPainter(self._item_picuture)

        for ix in range(min_ix, max_ix):
            bar_picture: QtGui.QPicture = self._bar_picutures[ix]

            if bar_picture is None:
                bar: BarData = self._manager.get_bar(ix)
                bar_picture = self._draw_bar_picture(ix, bar)
                self._bar_picutures[ix] = bar_picture

            bar_picture.play(painter)

        painter.end()

    def clear_all(self) -> None:
        """
        Clear all data in the item.
        """
        self._item_picuture = None
        self._bar_picutures.clear()
        self.update()


class CandleItem(ChartItem):
    """"""

    def __init__(self, manager: BarManager) -> None:
        """"""
        super().__init__(manager)

    def _draw_bar_picture(self, ix: int, bar: BarData) -> QtGui.QPicture:
        """"""
        # Create objects
        candle_picture: QtGui.QPicture = QtGui.QPicture()
        painter: QtGui.QPainter = QtGui.QPainter(candle_picture)

        # Set painter color
        if bar.close_price >= bar.open_price:
            painter.setPen(self._up_pen)
            painter.setBrush(self._black_brush)
        else:
            painter.setPen(self._down_pen)
            painter.setBrush(self._down_brush)

        # Draw candle shadow
        if bar.high_price > bar.low_price:
            painter.drawLine(
                QtCore.QPointF(ix, bar.high_price),
                QtCore.QPointF(ix, bar.low_price)
            )

        # Draw candle body
        if bar.open_price == bar.close_price:
            painter.drawLine(
                QtCore.QPointF(ix - BAR_WIDTH, bar.open_price),
                QtCore.QPointF(ix + BAR_WIDTH, bar.open_price),
            )
        else:
            rect: QtCore.QRectF = QtCore.QRectF(
                ix - BAR_WIDTH,
                bar.open_price,
                BAR_WIDTH * 2,
                bar.close_price - bar.open_price
            )
            painter.drawRect(rect)

        # Finish
        painter.end()
        return candle_picture

    def boundingRect(self) -> QtCore.QRectF:
        """"""
        min_price, max_price = self._manager.get_price_range()
        rect: QtCore.QRectF = QtCore.QRectF(
            0,
            min_price,
            len(self._bar_picutures),
            max_price - min_price
        )
        return rect

    def get_y_range(self, min_ix: int = None, max_ix: int = None) -> Tuple[float, float]:
        """
        Get range of y-axis with given x-axis range.

        If min_ix and max_ix not specified, then return range with whole data set.
        """
        min_price, max_price = self._manager.get_price_range(min_ix, max_ix)
        return min_price, max_price

    def get_info_text(self, ix: int) -> str:
        """
        Get information text to show by cursor.
        """
        bar: BarData = self._manager.get_bar(ix)

        if bar:
            words: list = [
                "Date",
                bar.datetime.strftime("%Y-%m-%d"),
                "",
                "Time",
                bar.datetime.strftime("%H:%M"),
                "",
                "Open",
                str(bar.open_price),
                "",
                "High",
                str(bar.high_price),
                "",
                "Low",
                str(bar.low_price),
                "",
                "Close",
                str(bar.close_price)
            ]
            text: str = "\n".join(words)
        else:
            text: str = ""

        return text


class VolumeItem(ChartItem):
    """"""

    def __init__(self, manager: BarManager) -> None:
        """"""
        super().__init__(manager)

    def _draw_bar_picture(self, ix: int, bar: BarData) -> QtGui.QPicture:
        """"""
        # Create objects
        volume_picture: QtGui.QPicture = QtGui.QPicture()
        painter: QtGui.QPainter = QtGui.QPainter(volume_picture)

        # Set painter color
        if bar.close_price >= bar.open_price:
            painter.setPen(self._up_pen)
            painter.setBrush(self._up_brush)
        else:
            painter.setPen(self._down_pen)
            painter.setBrush(self._down_brush)

        # Draw volume body
        rect: QtCore.QRectF = QtCore.QRectF(
            ix - BAR_WIDTH,
            0,
            BAR_WIDTH * 2,
            bar.volume
        )
        painter.drawRect(rect)

        # Finish
        painter.end()
        return volume_picture

    def boundingRect(self) -> QtCore.QRectF:
        """"""
        min_volume, max_volume = self._manager.get_volume_range()
        rect: QtCore.QRectF = QtCore.QRectF(
            0,
            min_volume,
            len(self._bar_picutures),
            max_volume - min_volume
        )
        return rect

    def get_y_range(self, min_ix: int = None, max_ix: int = None) -> Tuple[float, float]:
        """
        Get range of y-axis with given x-axis range.

        If min_ix and max_ix not specified, then return range with whole data set.
        """
        min_volume, max_volume = self._manager.get_volume_range(min_ix, max_ix)
        return min_volume, max_volume

    def get_info_text(self, ix: int) -> str:
        """
        Get information text to show by cursor.
        """
        bar: BarData = self._manager.get_bar(ix)

        if bar:
            text: str = f"Volume {bar.volume}"
        else:
            text: str = ""

        return text

class MACDItem(ChartItem):
    def __init__(self, manager: BarManager):
        super().__init__(manager)

    def _draw_bar_picture(self, ix: int, bar: BarData) -> QtGui.QPicture:
        """
        绘制特定bar对应的MACD柱形图，用柱子的高低表示MACD值的大小，正值柱子向上，负值柱子向下。
        """
        macd_picture = QtGui.QPicture()
        painter = QtGui.QPainter(macd_picture)

        macd_value, _ = self._manager.get_macd_data(ix)  # 获取MACD值，忽略信号线值

        # 根据MACD值正负来设置不同颜色和绘制方向
        if macd_value >= 0:
            bar_color = UP_COLOR
            bar_pen = pg.mkPen(color=bar_color, width=PEN_WIDTH)
            bar_brush = pg.mkBrush(color=bar_color)
            # 正值柱子从0往上绘制
            rect: QtCore.QRectF = QtCore.QRectF(
                ix - BAR_WIDTH / 2,  # 调整柱子中心位置
                0,
                BAR_WIDTH,
                macd_value
            )
        else:
            bar_color = DOWN_COLOR
            bar_pen = pg.mkPen(color=bar_color, width=PEN_WIDTH)
            bar_brush = pg.mkBrush(color=bar_color)
            # 负值柱子从0往下绘制（注意y坐标和高度的计算）
            rect: QtCore.QRectF = QtCore.QRectF(
                ix - BAR_WIDTH / 2,  # 调整柱子中心位置
                macd_value,
                BAR_WIDTH,
                -macd_value
            )
        painter.setPen(bar_pen)
        painter.setBrush(bar_brush)
        painter.drawRect(rect)

        painter.end()
        return macd_picture

    def _draw_bar_picture0(self, ix: int, bar: BarData) -> QtGui.QPicture:
        """
        绘制特定bar对应的MACD图形，例如绘制MACD线、信号线等，这里是简化示意，具体绘制逻辑根据MACD计算规则来细化
        """
        macd_picture = QtGui.QPicture()
        painter = QtGui.QPainter(macd_picture)

        # 获取该bar对应的MACD相关数据（这里假设已有获取的方法，比如从BarManager或者其他地方获取）
        macd_value, signal_value = self._manager.get_macd_data(ix)

        # 设置画笔等绘制相关属性，例如用不同颜色区分MACD线和信号线等
        macd_pen = pg.mkPen(color=UP_COLOR, width=PEN_WIDTH)  # 示例颜色，可调整
        signal_pen = pg.mkPen(color=DOWN_COLOR, width=PEN_WIDTH)  # 示例颜色，可调整

        painter.setPen(macd_pen)
        painter.drawLine(QtCore.QPointF(ix, macd_value), QtCore.QPointF(ix + 1, macd_value))  # 简单绘制MACD线，实际要考虑连续性等

        painter.setPen(signal_pen)
        painter.drawLine(QtCore.QPointF(ix, signal_value), QtCore.QPointF(ix + 1, signal_value))  # 简单绘制信号线

        painter.end()
        return macd_picture

    def boundingRect(self) -> QtCore.QRectF:
        """
        获取MACD指标展示图形的边界矩形，根据MACD数据的取值范围确定合适的y轴范围以及固定的x轴范围（取决于数据长度）
        """
        min_macd, max_macd = self._manager.get_macd_range()  # 修改此处，按照调整后的get_macd_range方法获取范围
        rect = QtCore.QRectF(
            0,
            min_macd,
            len(self._bar_picutures),
            max_macd - min_macd
        )
        return rect

    def get_y_range(self, min_ix: int = None, max_ix: int = None) -> Tuple[float, float]:
        """
        获取给定x轴范围对应的y轴（即MACD值）的范围，同样依赖BarManager获取相应范围数据
        """
        min_macd, max_macd = self._manager.get_macd_range(min_ix, max_ix)  # 修改此处，确保获取的是MACD线值范围
        return min_macd, max_macd

    def get_info_text(self, ix: int) -> str:
        """
        获取鼠标悬停在对应位置时显示的MACD相关信息文本，例如展示具体的MACD值、信号线值等
        """
        bar: BarData = self._manager.get_bar(ix)
        if bar:
            macd_value, signal_value = self._manager.get_macd_data(ix)
            words = [
                "Date",
                bar.datetime.strftime("%Y-%m-%d"),
                "",
                "Time",
                bar.datetime.strftime("%H:%M"),
                "",
                "MACD Value",
                str(macd_value),
                "",
                "Signal Line Value",
                str(signal_value)
            ]
            text = "\n".join(words)
        else:
            text = ""
        return text